action.script:
function self.drawing_script(drf,fr) local n=self.parent.parent.parent.n
local root=main() local sprites=root.stages.start.sprites;
local p=sprites.types.win_pattern; local drc=self.create_from_vault(p.graphics.main.get_drawing_id());
-- self.write(drc,0,"set_alpha",{128,{r=255,g=255,b=255}});
if(n==1) then ico=self.create("load",{"medias/icones/new.png"}) end if(n==2) then ico=self.create("load",{"medias/icones/open.png"}) end if(n==3) then ico=self.create("load",{"medias/icones/save.png"}) end if(n==4) then ico=self.create("load",{"medias/icones/options.png"}) end if(n==5) then ico=self.create("load",{"medias/icones/credits.png"}) end if(n==6) then ico=self.create("load",{"medias/icones/quit.png"}) end -- if(n~=5) then self.write(ico,0,"set_alpha",{255,{r=255,g=255,b=255}}); -- end self.compose(ico,0,drc,0,8,8);
self.write(drc,0,"set_alpha",{28*fr,{r=255,g=255,b=255}}); if (fr==7) then self.parent.parent.set_state("statique",{}) end return drc end

function self.drawing_script(drf,fr) local n=self.parent.parent.parent.n
if(n==1) then ico=self.create("load",{"medias/icones/new.png"}) end if(n==2) then ico=self.create("load",{"medias/icones/open.png"}) end if(n==3) then ico=self.create("load",{"medias/icones/save.png"}) end if(n==4) then ico=self.create("load",{"medias/icones/options.png"}) end if(n==5) then ico=self.create("load",{"medias/icones/credits.png"}) end if(n==6) then ico=self.create("load",{"medias/icones/quit.png"}) end self.write(ico,0,"set_alpha",{255,{r=255,g=255,b=255}}); self.compose(ico,0,drf,0,8,8);
self.write(drf,0,"set_alpha",{32*(8-fr),{r=255,g=255,b=255}}); if (fr==7) then self.parent.parent.set_state("statique",{}) end return drf end

graphic.script:
  self.set_cur_drawing (0);

  function self.on_update()
  local obj=self.parent.parent;
  self.move(obj.get_x(),obj.get_y())

end

self.set_cur_drawing (1);

function self.on_update()
local obj=self.parent.parent;
self.move(obj.get_x(),obj.get_y())

end

self.set_cur_drawing (2);

function self.on_update()
local obj=self.parent.parent;
self.move(obj.get_x(),obj.get_y())

end

self.set_cur_drawing (3);

function self.on_update()
local obj=self.parent.parent;
self.move(obj.get_x(),obj.get_y())

end

sprite_type.script:
function self.show_all()
self.graphics.coin1.show();
self.graphics.coin2.show();
self.graphics.coin3.show();
self.graphics.coin4.show();

end

function self.hide_all()
self.graphics.coin1.hide();
self.graphics.coin2.hide();
self.graphics.coin3.hide();
self.graphics.coin4.hide();

end

function self.move_from_selection(x,y,w,h)
self.jump(x,y)
self.graphics.coin3.move(x,y);
self.graphics.coin4.move(x+w,y);
self.graphics.coin1.move(x+w,y+h);
self.graphics.coin2.move(x,y+h);

end

function self.goto(n)
local st=self.parent.parent;

if (n==7) then
st.current_button=1;
else
if (n==0) then
st.current_button=6;
else
st.current_button=n;
end
end
but=st.buttons[st.current_button];
self.move_from_selection(but[2]-7,but[3]-7,64+7,64+7)
end

interaction.script:
function self.on_keypress(e)
 if e=="echap" then exit(2) end;
end

stage.script:
      self.current_button=1;
      self.buttons={
      {"b1",100+160,68+120},
      {"b2",170+160,68+120},
      {"b3",240+160,68+120},
      {"b4",100+160,140+120},
      {"b5",170+160,140+120},
      {"b6",240+160,140+120}
      };

      function self.fadout()
      local sprites=self.sprites;
      n=1
      while n < 7 do
	local button=self.buttons[n]
        sprites[button[1]].states.set_state ("fadout",{});
        n=n+1
      end		  

      end

      function self.on_load()
      local sprites=self.sprites;


      sprites.add_sprite_from_type("bg",160,120);

--      sprites.add_sprite_named_from_type("selection","select_box",48,48);
--      sprites.selection.goto(1)

n=1
      while n < 7 do
	local button=self.buttons[n]
	sprites.add_sprite_named_from_type(button[1],"win",button[2],button[3]);
        sprites[button[1]].n=n

        sprites[button[1]].states.set_state ("fadin",{});

      n=n+1
      end		  

--      sprites[b1].states.set_state ("bouge",{translation={30,{x=48,y=-64}}});

      end
